#!/bin/bash

# Script to automatically extract and display migration SQL files
# Makes it easy to copy/paste into Supabase SQL Editor

echo "📋 APR Schema Migration SQL Files"
echo "=================================="
echo ""

MIGRATIONS_DIR="supabase/migrations"

if [ ! -d "$MIGRATIONS_DIR" ]; then
    echo "❌ Migrations directory not found: $MIGRATIONS_DIR"
    exit 1
fi

# Get migration files in order
MIGRATIONS=($(ls -1 "$MIGRATIONS_DIR"/*.sql | sort))

if [ ${#MIGRATIONS[@]} -eq 0 ]; then
    echo "❌ No migration files found"
    exit 1
fi

echo "Found ${#MIGRATIONS[@]} migration file(s):"
echo ""

for i in "${!MIGRATIONS[@]}"; do
    file="${MIGRATIONS[$i]}"
    filename=$(basename "$file")
